import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DecConAjout.css';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { addEmployee } from '../../services/api';

function DecConAjout() {
  const [formData, setFormData] = useState({
    nom: '',
    postNom: '',
    prenom: '',
    sex: '',
    dateDeNaissance: '',
    villeDeNaissance: '',
    dateEngagement: '',
    numeroDeTelephone: '',
    role: '',
    departement: '',
    address: '',
    quartier: '',
    commune: '',
    ville: '',
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Validate form inputs
  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((field) => {
      if (!formData[field]) {
        newErrors[field] = 'This field is required';
      } else if (
        field === 'numeroDeTelephone' &&
        !/^\+?[0-9]{9,15}$/.test(formData[field])
      ) {
        newErrors[field] = 'Invalid phone number';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsSubmitting(true);
    try {
      await addEmployee(formData); // Call the API to add the employee
      alert('Employee added successfully!');
      navigate('/'); // Redirect to the home page
    } catch (error) {
      console.error('Error adding employee:', error);
      alert('Failed to add employee. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="conForm">
      <h2>Add New Employee</h2>
      <Form onSubmit={handleSubmit}>
        {/* Personal Information */}
        <Row className="mb-3">
          <Form.Group as={Col} controlId="formLastName">
            <Form.Label>Nom</Form.Label>
            <Form.Control
              name="nom"
              value={formData.nom}
              onChange={handleChange}
              isInvalid={!!errors.nom}
            />
            <Form.Control.Feedback type="invalid">{errors.nom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formPostName">
            <Form.Label>Post-Nom</Form.Label>
            <Form.Control
              name="postNom"
              value={formData.postNom}
              onChange={handleChange}
              isInvalid={!!errors.postNom}
            />
            <Form.Control.Feedback type="invalid">{errors.postNom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formFirstName">
            <Form.Label>Prénom</Form.Label>
            <Form.Control
              name="prenom"
              value={formData.prenom}
              onChange={handleChange}
              isInvalid={!!errors.prenom}
            />
            <Form.Control.Feedback type="invalid">{errors.prenom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formSex">
            <Form.Label>Sex</Form.Label>
            <Form.Select
              name="sex"
              value={formData.sex}
              onChange={handleChange}
              isInvalid={!!errors.sex}
            >
              <option value="">Choose...</option>
              <option value="M">M</option>
              <option value="F">F</option>
            </Form.Select>
            <Form.Control.Feedback type="invalid">{errors.sex}</Form.Control.Feedback>
          </Form.Group>
        </Row>

        {/* More fields */}
        {/* Add similar groupings as needed for other fields like "address," "role," and so on */}

        {/* Submission Button */}
        <div className="conFormButton">
          <Button variant="secondary" onClick={() => navigate('/')} disabled={isSubmitting}>
            Retour
          </Button>
          <Button variant="primary" type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Submitting...' : 'Submit'}
          </Button>
        </div>
      </Form>
    </div>
  );
}

export default DecConAjout;
