import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login/Login';
import Dashboard from './components/Dashboard/Dashboard';
import { getAllAdmins, getAllEmployees } from './services/api';

function App() {
  const [user, setUser] = useState(() => {
    const storedUser = localStorage.getItem('user');
    return storedUser ? JSON.parse(storedUser) : null;
  });

  const [admins, setAdmins] = useState([]);
  const [employees, setEmployees] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      if (user) {
        const [adminResponse, employeeResponse] = await Promise.all([getAllAdmins(), getAllEmployees()]);
        setAdmins(adminResponse.data);
        setEmployees(employeeResponse.data);
      }
    };

    fetchData();
  }, [user]);

  const handleLogin = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={user ? <Dashboard user={user} onLogout={handleLogout} admins={admins} employees={employees} /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
