import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DecConAjout.css';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { addEmployee } from '../../services/api';

function DecConAjout({}) {
  const [formData, setFormData] = useState({
    nom: '',
    postNom: '',
    prenom: '',
    sex: '',
    dateDeNaissance: '',
    villeDeNaissance: '',
    dateEngagement: '',
    numeroDeTelephone: '',
    role: '',
    departement: '',
    address: '',
    quartier: '',
    commune: '',
    ville: '',
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Validate form inputs
  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((field) => {
      if (!formData[field]) {
        newErrors[field] = 'This field is required';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   if (!validateForm()) return;

  //   try {
  //     console.log('Submitting data:', formData); // Debugging step
  //     const response = await addEmployee(formData); // Call the API to add the employee
  //     console.log('Response:', response); // Debugging step
  //     alert('Employee added successfully!');
  //     navigate('/'); // Redirect to home
  //   } catch (error) {
  //     console.error('Error adding employee:', error.response?.data || error.message); // Improved error logging
  //     alert('Failed to add employee. Please try again.');
  //   }
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const adminId = user._id; // Assuming `user` contains the logged-in admin's details
      const dataToSend = { ...formData, admin: adminId };
      await addEmployee(dataToSend); // Call the API to add the employee
      alert('Employee added successfully!');
      navigate('/'); // Redirect to home
    } catch (error) {
      console.error('Error adding employee:', error.response?.data || error.message);
      alert('Failed to add employee. Please try again.');
    }
  };



  return (
    <div className="conForm">
      <Form onSubmit={handleSubmit}>
        {/* Personal Information */}
        <Row className="mb-3">
          <Form.Group as={Col} controlId="formLastName">
            <Form.Label>Nom</Form.Label>
            <Form.Control
              name="nom"
              value={formData.nom}
              onChange={handleChange}
              isInvalid={!!errors.nom}
            />
            <Form.Control.Feedback type="invalid">{errors.nom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formPostName">
            <Form.Label>Post-Nom</Form.Label>
            <Form.Control
              name="postNom"
              value={formData.postNom}
              onChange={handleChange}
              isInvalid={!!errors.postNom}
            />
            <Form.Control.Feedback type="invalid">{errors.postNom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formFirstName">
            <Form.Label>Prénom</Form.Label>
            <Form.Control
              name="prenom"
              value={formData.prenom}
              onChange={handleChange}
              isInvalid={!!errors.prenom}
            />
            <Form.Control.Feedback type="invalid">{errors.prenom}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formSex">
            <Form.Label>Sex</Form.Label>
            <Form.Select
              name="sex"
              value={formData.sex}
              onChange={handleChange}
              isInvalid={!!errors.sex}
            >
              <option value="">Choose...</option>
              <option value="M">M</option>
              <option value="F">F</option>
            </Form.Select>
            <Form.Control.Feedback type="invalid">{errors.sex}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formDateDeNaissance">
            <Form.Label>Date de Naissance</Form.Label>
            <Form.Control
              type="date"
              name="dateDeNaissance"
              value={formData.dateDeNaissance}
              onChange={handleChange}
              isInvalid={!!errors.dateDeNaissance}
            />
            <Form.Control.Feedback type="invalid">{errors.dateDeNaissance}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} controlId="formVilleDeNaissance">
            <Form.Label>Ville de Naissance</Form.Label>
            <Form.Control
              name="villeDeNaissance"
              value={formData.villeDeNaissance}
              onChange={handleChange}
              isInvalid={!!errors.villeDeNaissance}
            />
