import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

const api = axios.create({
  baseURL: API_URL,
});

export const login = (email, password) => api.post('/hr/connecter', { email, motDePasse: password });
export const getAllEmployees = () => api.get('/employe');
export const addEmployee = (employeeData) => api.post('/employe/ajout', employeeData);
export const getHREmployees = (hrId) => api.get(`/employe/hr/${hrId}`);
export const getAllHR = () => api.get('/hr');
export const addHR = (hrData) => api.post('/hr/ajout', hrData);

export default api;
