// src/components/EmployeeList.js
import React, { useEffect, useState } from 'react';
import { getAllEmployees, getHREmployees } from '../../services/api';

const EmployeeList = ({ hrId }) => {
  const [employees, setEmployees] = useState([]);
  const [myEmployees, setMyEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      const response = await getAllEmployees();
      console.log('All employees:', response.data.employes); // Debugging log
      setEmployees(response.data.employes);
    };
    fetchEmployees();
  }, []);

  useEffect(() => {
    if (hrId) {
      const fetchMyEmployees = async () => {
        const response = await getHREmployees(hrId);
        console.log('My employees:', response.data.employe.employes); // Debugging log
        setMyEmployees(response.data.employe.employes);
      };
      fetchMyEmployees();
    }
  }, [hrId]);

  return (
    <div>
      <h2>All Employees</h2>
      <ul>
        {employees.map((employee) => (
          <li key={employee._id}>{`${employee.nom} ${employee.postNom} ${employee.preNom}`}</li>
        ))}
      </ul>

      {hrId && (
        <>
          <h2>My Employees</h2>
          <ul>
            {myEmployees.map((employee) => (
              <li key={employee._id}>{`${employee.nom} ${employee.postNom} ${employee.preNom}`}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default EmployeeList;
