import React from 'react';
import Tables from '../Tables/Tables';

const EmployeeList = ({ employees }) => {
  // Ensure 'employees' is an array before attempting to map over it
  if (!Array.isArray(employees)) {
    return <div>No employees available or data is still loading...</div>;
  }

  return (
    <div>
      <h2>Employee List</h2>
      <ul>
        {employees.map((employee) => (
          <li key={employee._id}>
            {/* Display employee details */}
            {`${employee.nom} ${employee.postNom} ${employee.preNom}`}
            {/* Pass individual employee data as props to Tables component */}
            <Tables
              nom={employee.nom}
              postNom={employee.postNom}
              preNom={employee.preNom}
              date={employee.dateDeNaissance}
              lieu={employee.villeDeNaissance}
              function={employee.role}
              adresse={employee.address}
              commune
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeList;
